jQuery.validator.setDefaults({
    onkeyup: !1,
    onfocusout: function(element) {
        $(element).valid();
    },
    highlight: function(element) {
        $(element).closest(".form-group").addClass("has-danger"), $(element).addClass("is-invalid");
    },
    unhighlight: function(element) {
        $(element).closest(".form-group").removeClass("has-danger"), $(element).removeClass("is-invalid");
    },
    errorElement: "div",
   // errorClass: "text-danger d-none",
    errorClass: "text-danger",
    errorPlacement: function(element, r) {
        r.parent(".input-group").length ? element.insertAfter(r.parent()) : element.insertAfter(r)

    }
});
